import { useEffect, useRef } from "react";

const CompanySliders = () => {
  const scrollRef = useRef(null);

  const companies = [
    {
      name: "Google",
      logo: "https://cdn.iconscout.com/icon/free/png-256/google-470-675827.png",
    },
    {
      name: "Accenture",
      logo: "/companylogo/accenture.png",
    },
    {
      name: "Deloitte",
      logo: "https://cdn.iconscout.com/icon/free/png-256/deloitte-282223.png",
    },
    {
      name: "TCS",
      logo: "data:image/jpeg;base64,",
    },
    {
      name: "Amazon",
      logo: "https://cdn.iconscout.com/icon/free/png-256/amazon-1869030-1583154.png",
    },
    {
      name: "Wipro",
      logo: "https://1000logos.net/wp-content/uploads/2021/05/Wipro-logo.png",
    },
    {
      name: "Infosys",
      logo: "https://upload.wikimedia.org/wikipedia/commons/thumb/9/95/Infosys_logo.svg/2560px-Infosys_logo.svg.png",
    },
    {
      name: "Capgemini",
      logo: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTDH3D_sFxInIWvLdh8-BK-xaKw7YpUcwhDaA&s",
    },
    { name: "Zscaler", logo: "data:image/jpeg;base64," },
  { name: "VMware", logo: "data:image/png;base64," },
  { name: "Salesforce", logo: "data:image/jpeg;base64," },
  { name: "ServiceNow", logo: "data:image/jpeg;base64," },
  { name: "Flipkart", logo: "data:image/jpeg;base64," },
  { name: "Paytm", logo: "data:image/jpeg;base64," },
  { name: "Zoho", logo: "data:image/png;base64," },
  { name: "Zeksta", logo: "data:image/png;base64," },
  
  { name: "Uber", logo: "data:image/png;base64," },
  { name: "Meesho", logo: "data:image/png;base64," },
  { name: "CRED", logo: "data:image/jpeg;base64," }
  ];
  

  // Duplicate the array for seamless infinite scroll
  const companyList = [...companies, ...companies];

  useEffect(() => {
    const scrollContainer = scrollRef.current;
    let scrollAmount = 0;

    const scrollLogos = () => {
      if (scrollContainer) {
        scrollAmount += 1;
        if (scrollAmount >= scrollContainer.scrollWidth / 2) {
          scrollAmount = 0;
        }
        scrollContainer.scrollLeft = scrollAmount;
      }
    };

    const interval = setInterval(scrollLogos, 20); // adjust speed here

    return () => clearInterval(interval);
  }, []);

  return (
    <div 
      
      className="bg-[url('/companylogo/sbc.jpg')] bg-cover bg-center bg-no-repeat py-12 px-4 "
    >
      
      
    <div className="overflow-hidden w-full bg-gray-50 py-6">
      <div
        className="flex gap-6 w-max animate-scroll-slow"
        ref={scrollRef}
      >
        {companyList.map((company, index) => (
          <div
            key={index}
            className="w-28 h-14 flex items-center justify-center bg-white shadow rounded-md p-2"
          >
            <img
              src={company.logo}
              alt={company.name}
              className="h-full w-auto object-contain"
            />
          </div>
        ))}
      </div>
    </div>
    </div>
  );
};

export default CompanySliders;
