import { useEffect, useRef } from "react";

const CompanySlider = () => {
  const scrollRef = useRef(null);

  const companies = [
    // New Companies
  {
    name: "Microsoft",
    logo: "https://cdn.iconscout.com/icon/free/png-256/microsoft-28-761688.png",
  },
  {
    name: "IBM",
    logo: "https://cdn.iconscout.com/icon/free/png-256/ibm-11-1175253.png",
  },
  {
    name: "Oracle",
    logo: "https://cdn.iconscout.com/icon/free/png-256/oracle-226048.png",
  },
  {
    name: "Cisco",
    logo: "https://cdn.iconscout.com/icon/free/png-256/cisco-226003.png",
  },
  
  
  {
    name: "Intel",
    logo: "https://cdn.iconscout.com/icon/free/png-256/intel-226007.png",
  },
  {
    name: "HCL",
    logo: "data:image/png;base64,",
  },
  {
    name: "SAP",
    logo: "https://cdn.iconscout.com/icon/free/png-256/sap-2-282596.png",
  },
  {
    name: "Tech Mahindra",
    logo: "data:image/png;base64,",
  },
  { name: "Zscaler", logo: "data:image/jpeg;base64," },
  { name: "VMware", logo: "data:image/png;base64," },
  { name: "Salesforce", logo: "data:image/jpeg;base64," },
  { name: "ServiceNow", logo: "data:image/jpeg;base64," },
  { name: "Flipkart", logo: "data:image/jpeg;base64," },
  { name: "Paytm", logo: "data:image/jpeg;base64," },
  { name: "Zoho", logo: "data:image/png;base64," },
  { name: "Zeksta", logo: "data:image/png;base64," },
  
  { name: "Uber", logo: "data:image/png;base64," },
  { name: "IBM", logo: "data:image/png;base64," },
  { name: "AMERICAN EXPRESS", logo: "data:image/png;base64," }
  ];

  // Duplicate the array for seamless infinite scroll
  const companyList = [...companies, ...companies];

  useEffect(() => {
    const scrollContainer = scrollRef.current;
    let scrollAmount = 0;

    const scrollLogos = () => {
      if (scrollContainer) {
        scrollAmount += 1;
        if (scrollAmount >= scrollContainer.scrollWidth / 2) {
          scrollAmount = 0;
        }
        scrollContainer.scrollLeft = scrollAmount;
      }
    };

    const interval = setInterval(scrollLogos, 20); // adjust speed here

    return () => clearInterval(interval);
  }, []);

  return (
    <div 
      
      className="bg-[url('/companylogo/sbc.jpg')] bg-cover bg-center bg-no-repeat py-12 px-4 "
    >
      <h1 className="text-center text-3xl md:text-5xl  text-black mb-8">
          Prepare For All Major Companies
        </h1>
      
    <div className="overflow-hidden w-full bg-gray-50 py-6">
      <div
        className="flex gap-6 w-max animate-scroll-slow"
        ref={scrollRef}
      >
        {companyList.map((company, index) => (
          <div
            key={index}
            className="w-28 h-14 flex items-center justify-center bg-white shadow rounded-md p-2"
          >
            <img
              src={company.logo}
              alt={company.name}
              className="h-full w-auto object-contain"
            />
          </div>
        ))}
      </div>
    </div>
    </div>
  );
};

export default CompanySlider;
